class Solution {
public:
    int minFlips(vector<vector<int>>& arr) {
        int row=arr.size(), col=arr[0].size() , count=0 ,cone=0 ,ans=0;
        for(int i=0;i<(row+1)/2;i++){
            for(int j=0;j<(col+1)/2;j++){
                int c1=0,c0=0;
                map<pair<int,int>,int> mp;
                mp[{i,j}]++;
                mp[{row-1-i,col-1-j}]++;
                mp[{row-1-i,j}]++;                
                mp[{i,col-1-j}]++;                
                for(auto it: mp){
                    if(arr[it.first.first][it.first.second]==0) c0++;
                    else c1++;
                }
                if(mp.size()==2){
                    if(c0==0) count++;
                    else{
                        if(c0==1 && c1==1){
                            ans++;
                            cone++;
                        }
                    }
                }
                else if(mp.size()==4) ans+=min(c0,c1);
                else ans+=c1; 
            }
        }

        if(count%2){
            if(cone <= 0)  return ans + 2;
            else return ans;
        }
        else return ans;
        return 0;
    }
};
