class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int m=a.size();
        int n=a[0].size();
        int ans=0;
        for (int i=0;i<m/2;i++){
            for (int j=0;j<n/2;j++){
                int cnt=0;
                if (a[i][j]) cnt++;
                if (a[m-1-i][j]) cnt++;
                if (a[i][n-1-j]) cnt++;
                if (a[m-1-i][n-1-j]) cnt++;
                ans+=min(cnt,4-cnt);
                a[i][j]=a[m-1-i][j]=a[i][n-1-j]=a[m-1-i][n-1-j]=0;
            }
        }
        int tot=0;
        bool ok=false;
        if (m%2){
            for (int j=0;j<n/2;j++){
                if (a[m/2][j] != a[m/2][n-1-j]) ans++,a[m/2][j]=a[m/2][n-1-j]=0,ok=true;
            }
        }
        if (n%2){
            for (int i=0;i<m/2;i++){
                if (a[i][n/2]!=a[m-1-i][n/2]) ans++,a[i][n/2]=a[m-1-i][n/2]=0,ok=true;
            }
        }
        tot=0;
        for (int i=0;i<m;i++) for (int j=0;j<n;j++) tot+=a[i][j];
        if (tot%4==1 || tot%4==3){
            tot--;
            ans++;
        }
        if (tot%4==0) return ans;
        if (ok) return ans;
        ans+=2;
        return ans;
    }
};
