class Solution {
public:
  int minFlips(vector<vector<int>> &grid)
{
    int n = grid.size();
    int m = grid[0].size();

    int urow = 0;
    int drow = n - 1;
    int lcol = 0;
    int rcol = m - 1;
    int ones, zeroes;
    int ans = 0;
    int flag=0;
    int totones=0;
    while (urow < drow)
    {

        lcol = 0;
        rcol = m - 1;

        while (lcol < rcol)
        {
             ones=0;
             zeroes=0;
            if (grid[urow][lcol] == 1)
            {
                ones++;
            }
            else
            {
                zeroes++;
            }

            if (grid[urow][rcol] == 1)
            {
                ones++;
            }
            else
            {
                zeroes++;
            }

            if (grid[drow][lcol] == 1)
            {
                ones++;
            }
            else
            {
                zeroes++;
            }

            if (grid[drow][rcol] == 1)
            {
                ones++;
            }
            else
            {
                zeroes++;
            }

            ans = ans + min(ones, zeroes);
            lcol++;
            rcol--;
        }

        if(lcol==rcol){

            if(grid[urow][lcol] ^ grid[drow][lcol]==1){
                ans=ans+1;
                flag=1;
                totones+2;

            }
            else{
                if(grid[urow][lcol]==1){
                    totones=totones+2;
                }
            }
        }
        urow++;
        drow--;
    }

    if(urow==drow){

        lcol=0;
        rcol=m-1;
        while (lcol<rcol)
        {
            if(grid[urow][lcol] ^ grid[drow][rcol]==1){
                ans=ans+1;
                flag=1;
                totones+2;

            }
            else{
                if(grid[urow][lcol]==1){
                    totones=totones+2;
                }
            }
            lcol++;
            rcol--;
        }

        if(lcol==rcol){
            if(grid[urow][lcol]==1){
                ans++;

            }
        }




    }
    if(totones%4==0){
        return ans;
    }
    else{
        if(flag==1){
            return ans;
        }
        else{
            return ans+2;
        }
    }

    return ans;
}
};
