class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {

        int i,j,m=grid.size(),n=grid[0].size(),ans=0,cnt=0,p=0;
        for(i=0;i<(m+1)/2;i++)
        {
            for(j=0;j<(n+1)/2;j++)
            {
               if((m%2==1&&i==m/2)&&(n%2==1&&j==n/2)){
                    ans+=grid[i][j];
                    continue;
               } 
             int k=0;
             k=grid[i][j]+grid[m-1-i][j]+grid[m-1-i][n-1-j]+grid[i][n-1-j];
             if((m%2==1&&i==m/2)||(n%2==1&&j==n/2))
             {
                k/=2;

                ans+=min(k,2-k);
                if(k==2) p+=2;
                if(k==1)cnt+=2;

             }else{
                // if(k>=2)cnt+=4;
                      ans+=min(k,4-k);
             }

            }
        }
        // ans=0;
        if(p%4==2&&cnt==0) ans+=2;



        return ans;
    }
};
