class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i = 0;i<n/2;i++){
            for(int j = 0;j<m/2;j++){
                int x = grid[i][j];
                int same = 0;
                if(grid[i][m-1-j] == x) same++;
                if(grid[n-1-i][j] == x) same++;
                if(grid[n-1-i][m-1-j] == x)same++;
                if(same <= 1){
                    ans += (same+1);
                }
                else{
                    ans += (3-same);
                }
            }
        }
        if(n%2 == 1 && m%2==0){
            int total = 0;
            int sum = 0;
            for(int i = 0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-1-i]) total++;
                else{
                    sum += 2*(grid[n/2][i]);
                }
            }
            ans += total;
            if(total == 0) ans += (sum%4);
        }
        if(n%2 == 0 && m%2 == 1){
            int total = 0;
            int sum = 0;
            for(int i = 0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]) total++;
                else{
                    sum += 2*(grid[i][m/2]);
                }
            }
            ans += total;
            if(total == 0) ans += (sum%4);
        }
        if(n%2 == 1 && m%2 == 1){
            int total = 0;
            int sum = 0;
            for(int i = 0;i<n/2;i++){
                if(grid[i][m/2] != grid[n-1-i][m/2]) total++;
                else{
                    sum += 2*(grid[i][m/2]);
                }
            }
            for(int i = 0;i<m/2;i++){
                if(grid[n/2][i] != grid[n/2][m-1-i]) total++;
                else{
                    sum += 2*(grid[n/2][i]);
                }
            }
            ans += total;
            if(total == 0) ans += (sum%4);
            if(grid[n/2][m/2] == 1) ans++;
        }
        return ans;
    }
};
