class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        int cnt = 0;
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int one = 0,zero;
                if(grid[i][j]==1) one++;
                if(grid[i][m-j-1]==1) one++;
                if(grid[n-i-1][j]==1) one++;
                if(grid[n-i-1][m-j-1]==1) one++;
                zero = 4 - one;
                ans += min(zero,one);
            }
        }
        int val = 0;
        if(n%2){
            for(int j=0;j<m/2;j++){
                if(grid[n/2][j]==1 && grid[n/2][m-j-1]==1){
                    val+=2;
                }
                if(grid[n/2][j] != grid[n/2][m-j-1]){
                    cnt+=1;
                }
            }
        }
        if(m%2){
            for(int i=0;i<n/2;i++){
                if(grid[i][m/2]==1 && grid[n-i-1][m/2]==1){
                    val+=2;
                }
                if(grid[i][m/2] != grid[n-i-1][m/2]){
                    cnt+=1;
                }
            }
        }
        ans+=cnt;
        if(val%4){
            int rem = val%4;
            if(rem>cnt*2){
                ans+=rem;
            }
        }
        if((n%2) && (m%2)){
            if(grid[n/2][m/2]) ans++;
        }
        return ans;
    }
};
