class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i=0; i<n/2; ++i){
            for(int j=0; j<m/2; ++j){
                int lCount = 0;
                lCount += grid[i][j];
                lCount += grid[i][m-1-j];
                lCount += grid[n-1-i][j];
                lCount += grid[n-1-i][m-1-j];
                ans += min(4-lCount,lCount);
            }
        }
        bool alert=true;
        int count=0;
        if ((n%2)==1){
            for(int j=0; j<(m/2); ++j){
                int lCount = 0;
                lCount += grid[n/2][j];
                lCount += grid[n/2][m-1-j];
                if (lCount==1){
                    alert = false;
                }
                count += (lCount==2);
                ans += min(2-lCount,lCount);
            }
        }
        if ((m%2)==1){
            for(int j=0; j<(n/2); ++j){
                int lCount = 0;
                lCount += grid[j][m/2];
                lCount += grid[n-1-j][m/2];
                if (lCount==1){
                    alert = false;
                }
                count += (lCount==2);
                ans += min(2-lCount,lCount);
            }
        }
        if (((n%2)==1)&&((m%2)==1)){
            ans += grid[n/2][m/2];
        }
        if(alert==true){
            if((count%2)==1){
                ans += 2;
            }
        }
        return ans;
    }
};
