class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int r4 = 0;
        int r2c = 0;
        int r2i = 0;
        int r1i = 0;
        int r1c = 0;
        // int r2 = 0;
        int r1 = 0;
        int n = grid.size();
        int m = grid[0].size();
        for(int i = 0; i < n; ++i) {
            for(int j = 0; j < m; ++j) {
                int t = grid[i][j] + grid[n - 1- i][j] + grid[n - 1-i][m - 1- j] + grid[i][m - 1-j];
                if(i == n - 1 - i && j == m - 1 - j) {
                    // t /= 4;
                    // if(t) r1c++;
                    // else r1i++;
                    r1 += t / 4;;
                }
                else if(i == n - 1 - i || j == m - 1 - j) {
                    t /= 2;
                    if(t == 2) r2c++;
                    else if(t) r2i++;
                    // r2 += t / 2;
                }
                else r4 += min(4 - t, t);
            }
        }

        cout << r4 / 4 << " " << r2c / 2 << " " << r2i / 2 << " " << r1 << endl;
        int ans = r4 / 4 + r1;
        if((r2c / 2) % 2) { 
            if(r2i) {
                r2i -= 2;
                ans++;
            }
            else ans += 2;
        }

        ans += (r2i / 2);



        return ans;
    }
};
