class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
      int res = 0, res2 = 0, ones = 0;
      int n = grid.size();
      int m = grid[0].size();

        for(int i = 0; i<n/2; i++) {
          for(int j = 0; j<m/2; j++) {
            int zer = 0;
            if(grid[i][j] == 0) zer++;
            if(grid[n-1-i][j] == 0) zer++;
            if(grid[i][m-1-j] == 0) zer++;
            if(grid[n-1-i][m-1-j] == 0) zer++;
            if(zer == 2) res += 2;
            if(zer == 1 or zer == 3) res += 1;
          }
        }

        if(n%2 and m%2) {
          if(grid[n/2][m/2]) res++;
        }
        if(n%2) {
          for(int i = 0; i<m/2; i++) {
            if(grid[n/2][i] != grid[n/2][m-i-1]) res2++;
             else if(grid[n/2][m-i-1] == 1) ones += 2;
          }
        }
        if(m%2) {
          for(int i = 0; i<n/2; i++) {
              // cout<<i<<"   "<<n<<"   "<<m<<endl;
              // if(grid[i][m/2])
            if(grid[i][m/2] != grid[n-1-i][m/2]) res2++;
            else if(grid[n-1-i][m/2] == 1) ones += 2;
          }
        }

        if(ones%4 == 2 and res2 == 0) {
            res += 2;
        }

        return res + res2;
    }
};
