class Solution:
    def minFlips(self, A: List[List[int]]) -> int:
        m,n = len(A),len(A[0])
        csum = changes = zeroones = 0
        for i in range((m+1)//2):
            for j in range((n+1)//2):
                I = m-1-i; J = n-1-j
                if i < m-1-i and j < n-1-j:
                    ones = A[i][j] + A[I][j] + A[i][J] + A[I][J]
                    changes += min(ones,4-ones)
                elif i < I and j == J:
                    ones = A[i][j] + A[I][j]
                    changes += 1 if ones == 1 else 0
                    csum += 2 if ones == 2 else 0
                    if ones == 1:
                        zeroones += 1
                elif i == I and j < J:
                    ones = A[i][j] + A[i][J]
                    changes += 1 if ones == 1 else 0
                    csum += 2 if ones == 2 else 0
                    if ones == 1:
                        zeroones += 1
                else:
                    changes += A[i][j]
        if csum % 4 == 0:
            return changes
        if zeroones > 0:
            return changes
        return changes+2
