class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int x = grid[0].size();
        int y = grid.size();
        int count =0;
        for(int i=0;i<(grid.size())/2;i++)
        {
            for(int j=0;j<(grid[0].size())/2;j++)
            {
                int temp = grid[i][j]+grid[y-1-i][x-1-j]+grid[y-1-i][j]+grid[i][x-1-j];
                if(temp==0||temp==4)
                    continue;
                else if(temp==1||temp==3)
                    count++;
                else if(temp==2)
                {
                    count+=2;
                }

            }
        }
        if(x%2!=0&&y%2!=0)
        {
            int need = 0;
            int k = 0;
            for(int i=0;i<y/2;i++)
            {
                if(grid[i][x/2]==1&&grid[y-1-i][x/2])
                {
                    need++;
                }
                if(grid[i][x/2]!=grid[y-1-i][x/2])
                {
                    k++;
                }
            }
            for(int i=0;i<x/2;i++)
            {
                if(grid[y/2][i]==1&&grid[y/2][x-1-i])
                {
                    need++;
                }
                if(grid[y/2][i]!=grid[y/2][x-1-i])
                {
                    k++;
                }
            }
            if(need%2==1&&k==0)
                count+=2;
            count+=k;
            count+=grid[y/2][x/2];
        }
        else if(x%2!=0)
        {
            int need = 0;
            int k = 0;
            for(int i=0;i<y/2;i++)
            {
                if(grid[i][x/2]==1&&grid[y-1-i][x/2])
                {
                    need++;
                }
                if(grid[i][x/2]!=grid[y-1-i][x/2])
                {
                    k++;
                }
            }
            if(need%2==1&&k==0)
                count+=2;
            count+=k;
        }
        else if(y%2!=0)
        {
            int need = 0;
            int k = 0;
            for(int i=0;i<x/2;i++)
            {
                if(grid[y/2][i]==1&&grid[y/2][x-1-i])
                {
                    need++;
                }
                if(grid[y/2][i]!=grid[y/2][x-1-i])
                {
                    k++;
                }
            }
            if(need%2==1&&k==0)
                count+=2;
            count+=k;
        }
        return count;
    }
};
