class Solution {
    public int minFlips(int[][] grid) {
        int rows = grid.length;
        int cols = grid[0].length;

        int count=0;
        int set41=0;
        int set42=0;
        int set43=0;

        int set21=0;
        int set22=0;

        for(int i=0;i<(rows+1)/2;i++){
            for(int j=0;j<(cols+1)/2;j++){
                if(i==rows-1-i && j==cols-1-j){
                    if(grid[i][j] == 1) count++;
                }
                else if(i==rows-1-i){
                    int temp = grid[i][j] + grid[i][cols-1-j];
                    if(temp==1) set21++;
                    else if(temp==2) set22++;
                }
                else if(j==cols-1-j){
                    int temp = grid[i][j] + grid[rows-1-i][j];
                    if(temp==1) set21++;
                    else if(temp==2) set22++;
                }
                else{
                    int temp = grid[i][j] + grid[rows-1-i][j] + grid[i][cols-1-j] + grid[rows-1-i][cols-1-j] ;
                    if(temp==1) set41++;
                    else if(temp==2) set42++;
                    else if(temp==3) set43++;
                }                                    
            }
        }
        count+=(set41+set43 + 2*set42 + set21);

        if(set22%2!=0){
            if(set21<=0) count+=2; 
        }

        return count;


    }
}
