class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        int ans = 0;
        for(int i = 0; i<n/2; i++){
            for(int j = 0; j<m/2; j++){
                int cnt = 0;
                if(grid[i][m-j-1]) cnt++;
                if(grid[i][j]) cnt++;
                if(grid[n-i-1][j]) cnt++;
                if(grid[n-i-1][m-j-1]) cnt++;
                grid[i][m-j-1] = 0;
                grid[i][j] = 0;
                grid[n-i-1][j] = 0;
                grid[n-i-1][m-j-1] = 0;
                ans += min(cnt,4-cnt);
            }
        }
        int cnt1 = 0;
        int cnt2 = 0;
        int opcnt1 = 0;
        int opcnt2 = 0;
        if(n&1){
            int cnt = 0;
            for(int i = 0; i<m/2; i++){
                if(grid[n/2][i] != grid[n/2][m-i-1]){
                    cnt++;
                }   
                else if(grid[n/2][i] == 1) cnt1++;
            }
            opcnt1 = cnt;
            ans+= cnt;
        }
        if(m&1){
            int cnt = 0;
            for(int i = 0; i<n/2; i++){
                if(grid[i][m/2] != grid[n-i-1][m/2]) cnt++;
                else if(grid[i][m/2] == 1) cnt2++;
            }
            opcnt2 = cnt;
            ans+=cnt;
        }
        cnt1*=2;
        cnt2*=2;
        if(m&1 && n&1) ans += (grid[n/2][m/2]);
        if(opcnt1+opcnt2 > 0) return ans;
        else{
            if((cnt1+cnt2)%4 == 0) return ans;
            else return ans + 2;
        }
    }
};
