class Solution {
public:

    int func(vector<vector<int>>& grid){
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i=0;i<m/2;i++){
            for(int j=0;j<n/2;j++){
                int count = grid[i][j];
                count += grid[m-1-i][j];
                count += grid[i][n-1-j];
                count += grid[m-1-i][n-1-j];
                ans += min(4-count,count);
            }
        }
        return ans;
    }

    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int p = func(grid);
        if(m%2 == 0 && n%2 == 0){
            return p;
        }
        if(m%2 == 1 && n%2 == 0){
            int count1 = 0;
            int mism = 0;
            for(int j=0;j<n;j++){
                count1 += grid[m/2][j];
            }
            for(int j=0;j<n/2;j++){
                if(grid[m/2][j] != grid[m/2][n-1-j]){
                    mism++;
                }
            }
            return p + max(mism,min(count1%4,4-count1%4));
        }
        if(m%2 == 0 && n%2 == 1){
            int count1 = 0;
            int mism = 0;
            for(int i=0;i<m;i++){
                count1 += grid[i][n/2];
            }
            for(int i=0;i<m/2;i++){
                if(grid[i][n/2] != grid[m-1-i][n/2]){
                    mism++;
                }
            }
            return p + max(mism,min(count1%4,4-count1%4));
        }
        int count1 = 0;
        int mism = 0;
        for(int i=0;i<m;i++){
            if(i == m/2){
                continue;
            }
            count1 += grid[i][n/2];
        }
        for(int j=0;j<n;j++){
            if(j== n/2 ){
                continue;
            }
            count1 += grid[m/2][j];
        }
        for(int j=0;j<n/2;j++){
            if(grid[m/2][j] != grid[m/2][n-1-j]){
                mism++;
            }
        }
        for(int i=0;i<m/2;i++){
            if(grid[i][n/2] != grid[m-1-i][n/2]){
                mism++;
            }
        }
        // cout << p << endl;
        // cout << mism << endl;
        // cout << count1 << endl;
        return p + max(mism,min(count1%4,4-count1%4)) + grid[m/2][n/2];


    }
};
