class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        count = 0
        for i in range(m // 2):
            for j in range(n // 2):
                s = grid[i][j] + grid[~i][j] + grid[i][~j] + grid[~i][~j]
                if s >= 2:
                    count += 4 - s
                else:
                    count += s
        c = [0] * 3
        if m % 2:
            for j in range(n // 2):
                s = grid[m // 2][j] + grid[m // 2][~j]
                c[s] += 1
        if n % 2:
            for i in range(m // 2):
                s = grid[i][n // 2] + grid[~i][n // 2]
                c[s] += 1
        count += c[1]
        if c[2] % 2:
            if not c[1]:
                count += 2
        if m % 2 and n % 2 and grid[m // 2][n // 2] == 1:
            count += 1
        return count
