class Solution {
public:
    int find(vector<vector<int> > &A, int l, int r, int m)
    {
        int ans = 0;
        for(int j=0; j<m/2; j++)
        {
            map<int, int> mp;
            mp[A[l][j]]++; mp[A[l][m-1-j]]++; 
            mp[A[r][j]]++; mp[A[r][m-1-j]]++;

            if(mp.size() > 1) {
                ans += min(mp[0], mp[1]);
            }
        }
        return ans;
    }

    vector<int> single(vector<int> &v)
    {
        int n = v.size();
        int a = 0; int b = 0; int c = 0;

        for(int i=0; i<n/2; i++) {
            if(v[i] == v[n-1-i]) {
                if(v[i] == 0) a++;
                else b++;
            } else {
                c++;
            }
        }

        vector<int> ans;
        ans.push_back(a); ans.push_back(b); ans.push_back(c);
        return ans;
    }

    int minFlips(vector<vector<int>>& A) {
        int n = A.size(); int m = A[0].size();
        int ans = 0;

        int i = 0; int j = n-1;
        while(i < j) {
            ans += find(A, i, j, m);
            i++; j--;
        }

        if(n%2 == 0 && m%2 == 0) {
            return ans;
        } 

        int a = 0; int b = 0; int c = 0;
        if(m%2 == 1) {
            vector<int> v;
            for(int k=0; k<n; k++) v.push_back(A[k][m/2]);
            vector<int> B = single(v);
            a += B[0]; b += B[1]; c += B[2];
        }

        if(n%2 == 1) {
            vector<int> v;
            for(int k=0; k<m; k++) v.push_back(A[n/2][k]);
            vector<int> B = single(v);
            a += B[0]; b += B[1]; c += B[2];
        }

        if(n%2 == 1 && m%2 == 1) {
            ans += A[n/2][m/2];
        }

        ans += c;

        // cout<<a<<" "<<b<<" "<<c<<endl;
        // cout<<ans<<endl;

        b = b*2;

        if(b%4 != 0) {
            if(c == 0) {
                ans += 2;
            }
        }

        return ans;
    }
};
