class Solution {
public:


    int minFlips(vector<vector<int>>& grid) {
        int cnt = 0;
        int row, col, rrow, rcol;

        // non-center rows and columns
        for(row=0; row<(grid.size()/2); row++){
            for(col=0; col<(grid[0].size()/2); col++){
                rrow = (grid.size()-1)-row;
                rcol = (grid[0].size()-1)-col;

                int num_ones = grid[row][col]+grid[rrow][col]+grid[row][rcol]+grid[rrow][rcol];
                cnt += min(num_ones, 4-num_ones);
            }
        }

        //cout << cnt << "\n";

        // center point
        if( ((grid[0].size())%2 == 1) && ((grid.size())%2 == 1) ){
            if((grid[grid.size()/2][grid[0].size()/2]) == 1) cnt++;
        }

        //cout << cnt << "\n";

        // center row
        int num_ones = 0;
        int crc_chg = 0;

        row = grid.size()/2;
        if(grid.size()%2 == 1){
            for(col=0; col<(grid[0].size()/2); col++){
                rcol = (grid[0].size()-1)-col;
                if(grid[row][col] != grid[row][rcol]){
                    crc_chg = 1;
                    cnt++;
                }
                num_ones += 2*grid[grid.size()/2][col];
            }
        }

        // center column
        col = grid[0].size()/2;
        if(grid[0].size()%2 == 1){
            for(row=0; row<(grid.size()/2); row++){
                rrow = grid.size()-1-row;
                if(grid[row][col] != grid[rrow][col]){
                    crc_chg = 1;
                    cnt++;
                }
                num_ones += 2*grid[row][col];
            }
        }

        if(crc_chg != 1){
            num_ones = num_ones%4;
            cnt += num_ones;
        }

        //cout << cnt << "\n";

        return cnt;
    }
};
