class Solution {
public:
    int solve(int idx,vector<pair<int,int>>&v,int r,vector<vector<int>>&dp){
        if(idx>=v.size()){
            if(r==0)
            return 0;
            return 1e9;
        }
        if(dp[idx][r]!=-1){
            return dp[idx][r];
        }
        int a=v[idx].first+solve(idx+1,v,(r+v[idx].second+v[idx].first)%4,dp);
        int b=v[idx].second+solve(idx+1,v,r,dp);
        return dp[idx][r]= min(a,b);
    }
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int i=0,j=n-1;
        int ans=0;
        vector<pair<int,int>>v;
        while(i<j){
            int l=0,r=m-1;
            while(l<r){
                int cnt0=0,cnt1=0;
                if(grid[i][l]==0)cnt0++;
                else cnt1++;
                if(grid[i][r]==0)cnt0++;
                else cnt1++;
                if(grid[j][l]==0)cnt0++;
                else cnt1++;
                if(grid[j][r]==0)cnt0++;
                else cnt1++;
                v.push_back({cnt0,cnt1});
                l++,r--;
            }
            if(l==r){
                int cnt0=0,cnt1=0;
                if(grid[i][l]==0)cnt0++;
                else cnt1++;
                if(grid[j][r]==0)cnt0++;
                else cnt1++;
                 v.push_back({cnt0,cnt1});
            }
            i++;
            j--;
        }
        if(i==j){
             int l=0,r=m-1;
            while(l<r){
                int cnt0=0,cnt1=0;
                if(grid[i][l]==0)cnt0++;
                else cnt1++;
                if(grid[i][r]==0)cnt0++;
                else cnt1++; 
                v.push_back({cnt0,cnt1});
                l++,r--;
            }
            if(l==r){
                int cnt0=0,cnt1=0;
                if(grid[i][l]==0)cnt0++;
                else cnt1++; 
                 v.push_back({cnt0,cnt1});
            }
        }
         int sz=v.size();
        vector<vector<int>>dp(sz,vector<int>(4,-1));
        return solve(0,v,0,dp);
    }
};
