class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        n=len(grid)
        m=len(grid[0])
        ans=0
        if (n%2==1 and m%2==1):
            if (grid[n//2][m//2]==1):
                ans+=1
        for i in range(n//2):
            for j in range(m//2):
                c=0
                c+=grid[i][j]
                c+=grid[n-i-1][j]
                c+=grid[n-i-1][m-1-j]
                c+=grid[i][m-1-j]
                if c<2:
                    ans+=c
                else:
                    ans+=(4-c)
        tt=0
        t1=0
        t0=0
        tc=0
        if n%2==1:
            for i in range(m//2):
                if (grid[n//2][i]!=grid[n//2][m-i-1]):
                    ans+=1
                    tc=1
                    t1+=2
                else:
                    if (grid[n//2][i]==1):
                        t1+=2
        if (m%2==1):
            for i in range(n//2):
                if (grid[i][m//2]!=grid[n-i-1][m//2]):
                    ans+=1
                    tc=1
                    t1+=2
                else:
                    if (grid[i][m//2]==1):
                        t1+=2
        if (t1%4==2 and tc==0):
            ans+=2
        return ans
