class Solution {
public:
    int minFlips(vector<vector<int>>& v1) {
        int n=v1.size(),m=v1[0].size();
        int ans=0,cnt=0,num=0;
        for(int i=0;i<n/2;i++)
        {
            for(int j=0;j<m/2;j++)
            {
                int tmp=v1[i][j]+v1[n-1-i][j]+v1[i][m-j-1]+v1[n-i-1][m-j-1];
                if(tmp==0||tmp==4)
                {
                    continue;
                }
                else if(tmp==1)
                {
                    ans++;
                }
                else if(tmp==2)
                {
                    ans+=2;
                }
                else
                {
                    ans++;
                }
            }
        }
        if(n%2)
        {
            for(int i=0;i<m/2;i++)
            {
                if(v1[n/2][i]!=v1[n/2][m-1-i])
                {
                    ans++;
                    num+=2;
                }
                if(v1[n/2][i]==1&&v1[n/2][m-1-i]==1)
                {
                    cnt+=2;
                }
            }
        }
        if(m%2)
        {
            for(int i=0;i<n/2;i++)
            {
                if(v1[i][m/2]!=v1[n-1-i][m/2])
                {
                    ans++;
                    num+=2;
                }
                if(v1[i][m/2]==1&&v1[n-1-i][m/2]==1)
                {
                    cnt+=2;
                }
            }
        }
        if(n%2&&m%2)
        {
            if(v1[n/2][m/2]==1)
            {
                cnt++;
            }
        }
        if(cnt%4==0)
        {
            return ans;
        }
        else if(cnt%4==1)
        {
            ans++;
            return ans;
        }
        else if(cnt%4==3)
        {
            ans++;
            if(num>=2)
            {
                return ans;
            }
            ans+=2;
            return ans;
        }
        else
        {
            if(num>=2)
            {
                return ans;
            }
            ans+=2;
            return ans;
        }
    }
};
