class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m, n = len(grid), len(grid[0])
        ans = 0
        stat = 0
        used = False
        if m % 2 == 1:
            for j in range(n // 2):
                if grid[m // 2][j] != grid[m // 2][-1-j]:
                    ans += 1
                    used = True
                elif grid[m // 2][j] == 1:
                    stat += 2
        if n % 2 == 1:
            for i in range(m // 2):
                if grid[i][n // 2] != grid[-1-i][n // 2]:
                    ans += 1
                    used = True
                elif grid[i][n // 2] == 1:
                    stat += 2

        if m % 2 == 1 and n % 2 == 1 and grid[m // 2][n // 2] == 1:
            ans += 1

        if not used and stat % 4 == 2:
            ans += 2

        for i in range(m // 2):
            for j in range(n // 2):
                stat = [0, 0]
                stat[grid[i][j]] += 1
                stat[grid[i][-1-j]] += 1
                stat[grid[-1-i][j]] += 1
                stat[grid[-1-i][-1-j]] += 1
                ans += 4 - max(stat)

        return ans
