class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n = v.size(), m = v[0].size();
        int temp = 0, x = 0;
        for(int i = 0; i<n/2; i++)
            for(int j = 0; j<m/2; j++)
            {
                temp = 0;
                temp += v[i][j] + v[i][m-j-1];
                temp += v[n-i-1][j] + v[n-i-1][m-j-1];
                if(temp%4 == 0) continue;
                if(temp == 3)
                    x += 1;
                else
                    x += temp;
            }
        if(n%2 == 1 || m%2 == 1)
        {
            int singles = 0, total = 0;
            if(n%2 == 1)
            {
                for(int i = 0; i<m/2; i++)
                {
                    total += v[n/2][i] + v[n/2][m-i-1];
                    if(v[n/2][i] != v[n/2][m-i-1])
                        singles++;
                }
                // for(int i = 0; i<m; i++)
                //     total+=v[n/2][i];
            }
            if(m%2==1)
            {
                for(int i = 0; i<n/2; i++)
                {
                    total += v[i][m/2] + v[n-i-1][m/2];
                    if(v[i][m/2] != v[n-i-1][m/2])
                        singles++;
                }
                // for(int i = 0; i<n; i++)
                //     total += v[i][m/2];
            }
            if(n%2 == 1 && m%2==1)
                x+=v[n/2][m/2];

            x+=singles;

            if(((total + singles)%4 == 0) || ((total-singles)%4 == 0))
            {    
                return x;
            }
            if(singles == 0)
            {
                if(total%2==0)
                {
                    x+=2;
                    return x;
                }
                else
                {
                    total--;
                    x++;
                    if(total%4==0)
                        return x;
                    else
                        return x+2;
                }
            }


        }

        return x;
    }
};
