class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        int one=0;
        int c=0;
        int f=0;
         for(int i=0;i<(n)/2;i++){
            for(int j=0;j<(m)/2;j++){
                  int a=grid[i][j];
                  int b=grid[n-i-1][j];
                  int c=grid[i][m-j-1];
                  int d=grid[n-i-1][m-j-1];
                  int x=0,y=0;
                  if(a==0)x++;
                  else y++;
                  if(b==0)x++;
                  else y++;
                  if(c==0)x++;
                  else y++;
                  if(d==0)x++;
                  else y++;
                  if(x==y){
                    c+=2;
                  }
                  if(y>=x){
                    one+=4;
                  }
                  ans+=min(x,y);
            }
         }

         if(n%2==1){

                int i=(n)/2;
                for(int j=0;j<m/2;j++){
                    if(grid[i][j]!=grid[i][m-j-1]){
                        f++;
                        one+=2;
                        ans++;
                    }
                    else{
                        one+=grid[i][j];
                        one+=grid[i][j];
                    }
                }

         }


         if(m%2==1){

                int j=(m)/2;
                for(int i=0;i<n/2;i++){
                    if(grid[i][j]!=grid[n-i-1][j]){
                        ans++;
                        f++;
                        one+=2;

                        c++;
                    }else{
                        one+=grid[i][j];
                        one+=grid[i][j];
                    }
                }



         }

         int x=-1;
         if(n%2==1 && m%2==1){
            one+=grid[n/2][m/2];
            x=1;
         }



         if(one%4==0)return ans;
         if(one%4==2 && f>0)return ans;
         if(one%4==1 && x==1)return ans+1;
         if(one%4==3 && x==1 && f>0)return ans+1;

         return ans+one%4;






         return ans;
    }
};
