class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int operations = 0, n = grid.size(), m = grid[0].size(), one_one = 0, two_zero = 0, two_one = 0;

        for(int i = 0; i * 2 < n; i++) {
            for(int j = 0; j * 2 < m; j++) {
                int x = i, xx = (n - i - 1), y = j, yy = (m - j - 1);
                if(i != (n - i - 1) && j != (m - j - 1)) {
                    int ones = 0, zeros = 0;
                    if(grid[i][j] == 1) ones++;
                    else zeros++;
                    if(grid[n - i - 1][j] == 1) ones++;
                    else zeros++;
                    if(grid[i][m - j - 1] == 1) ones++;
                    else zeros++;
                    if(grid[n - i - 1][m - j - 1] == 1) ones++;
                    else zeros++;

                    operations += 4 - max(ones, zeros);
                }
                else if((i == (n - i - 1) || j == (m - j - 1)) && !(i == (n - i - 1) && j == (m - j - 1))) {
                    if(i == (n - i - 1)) {
                        if(grid[i][j] != grid[i][m - j - 1]) one_one++;
                        else if(grid[i][j] == 0) two_zero++;
                        else two_one++;
                    }
                    else {
                        if(grid[i][j] != grid[n - i - 1][j]) one_one++;
                        else if(grid[i][j] == 0) two_zero++;
                        else two_one++;
                    }
                }
                else {
                    // cout << "hehere" << endl;
                    if(grid[i][j] == 1)
                        operations++;
                }
            }
        }

        // cout << operations << ' ' << two_one << ' ' << two_zero << ' ' << one_one << endl;

        if(two_one % 2) {
            if(one_one > 0) operations += one_one;
            else operations += 2;
        }
        else operations += one_one;

        return operations;
    }
};
