class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        m,n=len(grid),len(grid[0])
        ans=0
        a,b=m//2,n//2
        for i in range(a):
            for j in range(b):
                x=grid[i][j]+grid[i][~j]+grid[~i][j]+grid[~i][~j]
                ans+=2-abs(x-2)
        sm=mflips=0
        if m&1:
            if n&1:
                if grid[a][b]==1:
                    ans+=1
                    grid[a][b]=0
            sm=sum(grid[a])%4
            mflips=0
            for j in range(b):
                if grid[a][j]!=grid[a][~j]:
                    #print(a,j,'flipm')
                    mflips+=1
        sn=nflips=0
        if n&1:
            sn=sum(grid[i][b] for i in range(m))%4
            nflips=0
            for i in range(a):
                if grid[i][b]!=grid[~i][b]:
                    #print(i,b,'flipn')
                    nflips+=1
        totalflips=nflips+mflips
        totalsum=sn+sm
        #print('preans',ans,totalsum,totalflips)
        ans+=totalflips
        if totalflips==0:
            ans+=2-abs(totalsum%4-2)
        else:
            #print((totalsum+totalflips)%2)
            ans+=(totalsum+totalflips)%2
        return ans
