class Solution {
public:
    int minFlips(vector<vector<int>>& a) 
    {
        int n = a.size() , m = a[0].size();
        int N = (n + 1)/2 , M = (m + 1) / 2;

        int ans = 0;
        for( int i = 0; i < n/2; i++ )
        {
            for( int j = 0; j < m/2; j++ )
            {
                int cnt1 = a[i][j] + a[i][m-j-1] + a[n-i-1][j] + a[n-i-1][m-j-1];
                ans += min( cnt1 , ( 4 - cnt1) );
            }
        }

        if( (n&1) && !(m&1) )
        {
            int cnt1 = 0 , op = 0;
            for( int j = 0; j < m/2; j++ ) 
            {
                cnt1 += (a[N-1][j] && a[N-1][m-j-1]);
                op += (a[N-1][j] != a[N-1][m-j-1]);
            }
            if( (cnt1&1) ) ans += (op ? 0 : 2);
            ans += op;
        }
        if( !(n&1) && (m&1) )
        {
            int cnt1 = 0 , op = 0;
            for( int j = 0; j < n/2; j++ ) 
            {
                cnt1 += (a[j][M-1] && a[n-j-1][M-1]);
                op += (a[j][M-1] != a[n-j-1][M-1]);
            }
            if( (cnt1&1) ) ans += (op ? 0 : 2);
            ans += op;
        }
        if( (n&1) && (m&1) )
        {
            int cnt1 = 0 , op = 0;
            for( int j = 0; j < m/2; j++ ) 
            {
                if( j == M-1 ) continue;
                cnt1 += (a[N-1][j] && a[N-1][m-j-1]);
                op += (a[N-1][j] != a[N-1][m-j-1]);
            }
            for( int j = 0; j < n/2; j++ ) 
            {
                if( j == N - 1 ) continue;
                cnt1 += (a[j][M-1] && a[n-j-1][M-1]);
                op += (a[j][M-1] != a[n-j-1][M-1]);
            }
            if( (cnt1&1) ) ans += (op ? 0 : 2);
            ans += op;
            ans += (a[N-1][M-1]);
        }
        return ans;
    }
};
