class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans=0;
        int n=grid.size();
        int m=grid[0].size();
        vector<int>v;
        int x=0,y=0;
        if(n%2)x=n/2+1;
        else x=n/2;
        if(m%2)y=m/2+1;
    else y=m/2;
        for(int i=0;i<x;i++)
        {
            for(int j=0;j<y;j++)
            {
                int cnt=0;
                if(grid[i][j]==1)
                {
                    cnt++;
                }
                if(grid[n-1-i][j]==1&&i!=n-1-i)
                {
                    cnt++;
                }
                if(grid[i][m-1-j]==1&&j!=m-1-j)
                {
                    cnt++;
                }
                if(grid[n-1-i][m-1-j]==1&&i!=n-1-i&&j!=m-1-j)
                {
                    cnt++;
                }
                int tot=0;
                if(i!=n-1-i&&j!=m-1-j)tot=4;
                else if(i!=n-1-i)tot=2;
                else if(j!=m-1-j)tot=2;
                else tot=1;
                if(tot==4)
                {
                    if(cnt>2)
                    {
                        ans+=(tot-cnt);
                    }
                    else
                    ans+=cnt;
                }
                else if(tot==2)v.push_back(cnt);
                else if(tot==1)ans+=cnt;
                //cout<<tot<<"-"<<cnt<<"-"<<ans<<endl;
            }
        }
        sort(v.begin(),v.end());
        int two=0;
        for(int i=v.size()-1;i>=0;i--)
        {
            if(v[i]==2&&two==0)
            {
                two++;
            }
            else if(v[i]==2&&two==1)
            {
                two=0;
            }
            else if(v[i]==1&&two==1)
            {
                ans+=1;
                two=0;
            }
            else if(v[i]==1&&two==0)
            {
                ans+=1;
            }
            else if(v[i]==0&&two==0){}
            else if(v[i]==0&&two==1)
            {
                ans+=2;
                two=0;
            }
        }
        ans+=two*2;
        return ans;
    }
};
