class Solution {
    public int minFlips(int[][] grid) {
        int startRow = 0,endRow = grid.length-1;
        int ans=0;
        while(startRow<endRow) {
            int startCol=0,endCol=grid[0].length-1;
            while(startCol<endCol) {
                ans+=findMinFlips(grid[startRow][startCol],grid[startRow][endCol],grid[endRow][startCol],grid[endRow][endCol]);
                startCol++;
                endCol--;
            }
            startRow++;
            endRow--;
        }
        int num = 0;
        int tempFlips =0;
        if(grid.length%2==1){
            int mid = (grid.length-1)/2;
            int startCol=0,endCol=grid[0].length-1;
            while(startCol<endCol) {
                if(grid[mid][startCol]==grid[mid][endCol]) {
                    if(grid[mid][startCol]==1) num++;
                }
                else {
                    tempFlips++;
                    ans++;
                }
                startCol++;
                endCol--;
            }
        }
        if(grid[0].length%2==1){
            int mid = (grid[0].length-1)/2;
            int startRow1 = 0,endRow1 = grid.length-1;
            while(startRow1<endRow1) {
                if(grid[startRow1][mid]==grid[endRow1][mid]){
                    if(grid[startRow1][mid]==1) num++;
                } 
                else {
                    tempFlips++;
                    ans++;
                }
                startRow1++;
                endRow1--;
            }
        }
        if((num%4==1 ||num%4==3) && tempFlips==0)
            ans+=2;
        if(grid.length%2==1 && grid[0].length%2==1) {
            ans+=grid[(grid.length-1)/2][(grid[0].length-1)/2];
        }
        return ans;
    }

    int findMinFlips(int a,int b,int c, int d) {
        int sum = a+b+c+d;
        return Math.min(sum,4-sum);
    }
}
