class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n = a.size(), m = a[0].size();
        int ans = 0;
        for(int i = 0; i < n / 2; i++){
            for(int j = 0; j < m / 2; j++){
                int d = (a[i][j] + a[n - 1 - i][j] + a[i][m - 1 - j] + a[n - 1 - i][m - 1 - j]);
                ans += min(d, (4 - d));
            }
        }
        int aa = 0, bb = 0;
        if(n & 1){
            for(int i = 0; i < m / 2; i++){
                if(a[n / 2][i] != a[n / 2][m - 1 - i]) bb += 1;
                if(a[n / 2][i] == 1 && a[n / 2][m - 1 - i] == 1) aa += 1;
            }
        }
        if(m & 1){
            for(int i = 0; i < n / 2; i++){
                if(a[i][m / 2] != a[n - 1 - i][m / 2]) bb += 1;
                if(a[i][m / 2] == 1 && a[n - 1 - i][m / 2] == 1) aa += 1;
            }
        }
        if(aa & 1){
            if(bb > 0) ans += bb;
            else ans += 2;
        }
        else ans += bb;
        if((n & 1) && (m & 1)) ans += a[n / 2] [m / 2];
        return ans;
    }
};
