class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n= grid.size();
        int ans=0;
        int m= grid[0].size();
        for(int i=0;i<(n/2);i++){
            for(int j=0;j<(m/2);j++){
                int one=0,zero=0;
                if(grid[i][j]==1) one++;
                else zero++;
                if(grid[i][m-j-1]==1)one++;
                else zero++;
                if(grid[n-1-i][j]==1) one++;
                else zero++;
                if(grid[n-1-i][m-j-1]==1)one++;
                else zero++; 

                ans+=min(one,zero);    
            }
        }
        int p1=0;
        int not_pair=0;
      if(n&1){

          int mid = (n/2);
          for(int j=0;j<m/2;j++){
              if(grid[mid][j]==grid[mid][m-1-j]){
                  if(grid[mid][j]==1) p1++;
              }else{
                  not_pair++;
              }
          } 
      }
      int p0=0;
        if(m&1){
            int mid = m/2;
          for(int j=0;j<n/2;j++){
              if(grid[j][mid]==grid[n-1-j][mid]){
                  if(grid[j][mid]==1) p0++;
              }else{
                  not_pair++;
              }
          }
        }
        ans+=not_pair;
        if((p0+p1)%2==1){
            if(not_pair>=1){
                not_pair-=2;
            }else{
                ans+=2;
            }
        }

        if(n%2==1 and m%2==1){
            if(grid[n/2][m/2]==1) ans++;
        }

        return ans;
    }
};
