class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows=0,cols=0;
        int n=grid.size();
        int m=grid[0].size();
        int kl=0;
        for(int i=0;i<=n/2;i++){
            for(int j=0;j<=m/2;j++){
                int l=grid[i][j]+grid[i][m-1-j]+grid[n-1-i][j]+grid[n-1-i][m-1-j];
                if(i!=n/2 && j!=m/2)rows+=min(l,4-l);
                else if(i==n/2 && j==m/2)rows+=min(l,4-l)/4;
                else if(i!=n/2 && j==m/2){
                    rows+=min(l,4-l)/2;
                    if(min(l,4-l)/2==1 )kl++;
                }
                else if(i==n/2 && j!=m/2){
                    rows+=min(l,4-l)/2;
                    if(min(l,4-l)/2==1 )kl++;
                }
                if(l>4-l){
                    grid[i][j]=1;
                    grid[i][m-1-j]=1;
                    grid[n-1-i][j]=1;
                    grid[n-1-i][m-1-j]=1;
                }
                else{
                    grid[i][j]=0;
                    grid[i][m-1-j]=0;
                    grid[n-1-i][j]=0;
                    grid[n-1-i][m-1-j]=0;
                }
            }
        }
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                cols+=grid[i][j];
                //cout<<grid[i][j]<<" ";
            }
        }
        int ll=cols%4;
        if(ll==3 && kl>0) ll=1;
        else if(ll==2 && kl>0) ll=0;
        //cout<<ll<<endl;
        return rows+ll;
    }
};
