class Solution {
public:
    int minFlips(vector<vector<int>>& a) {
        int n=a.size() , m=a[0].size() ,p=0 , zerooo=0 , fin=0;

        for(int i=0;i<(n+1)/2;i++)
        {
             for(int j=0;j<(m+1)/2;j++)
             {
                map<pair<int,int>,int> mpp;
                mpp[{i , j}]++;
                mpp[{n-1-i , j}]++;
                mpp[{n-1-i , m-1-j}]++;
                mpp[{i , m-1-j}]++;

                 int one=0 , zero=0; 
                 for(auto i:mpp)
                 {
                     if(a[i.first.first][i.first.second]==0)
                     {
                         zero++;
                     }
                     else
                     {
                         one++;

                     }
                 }

                 if(mpp.size()==4){
                    fin+=min(zero,one);
                }
                 else if(mpp.size()==2){
                    if(one==2){
                        p++;
                    }
                     else{
                        if(zero==1 && one==1){
                            fin++;
                            zerooo++;
                        }
                    }
                 }
                 else
                 {
                     fin+=one;
                 }
             }
        }


        if(p%2==1)
        {

             if(zerooo>0)
                return fin;
            else
                return fin+2;



        }
        else
        {
           return fin;
        }
    return 0;
    }

};
