class Solution {
    public int minFlips(int[][] grid) {
        int m = grid.length, n = grid[0].length;
        int res = 0;
        for (int i = 0; i < m / 2; i++)
            for (int j = 0; j < n / 2; j++) {
                int cnt0 = 0, cnt1 = 0;
                if (grid[i][j] == 0) cnt0++;
                else cnt1++;
                if (grid[m - 1 - i][j] == 0) cnt0++;
                else cnt1++;
                if (grid[i][n - 1 - j] == 0) cnt0++;
                else cnt1++;
                if (grid[m - 1 - i][n - 1 - j] == 0) cnt0++;
                else cnt1++;
                if (cnt0 == 0 || cnt1 == 0) continue;
                res += Math.min(cnt0, cnt1);
            }
        if (m % 2 == 1 && n % 2 == 1) {
            int cnt1 = 0, t = 0;
            int l = 0, r = n - 1;
            while (l < r) {
                if (grid[m / 2][l] != grid[m / 2][r]) t++;
                else if (grid[m / 2][l] == 1) cnt1 += 2;
                l++;
                r--;
            }
            l = 0;
            r = m - 1;
            while (l < r) {
                if (grid[l][n / 2] != grid[r][n / 2]) t++;
                else if (grid[l][n / 2] == 1) cnt1 += 2;
                l++;
                r--;
            }
            res += t;
            if (cnt1 % 4 != 0 && t == 0) res += 2;
            res += (grid[m / 2][n / 2] == 1 ? 1 : 0);
        } else if (m % 2 == 1) {
            int cnt1 = 0, t = 0;
            int l = 0, r = n - 1;
            while (l < r) {
                if (grid[m / 2][l] != grid[m / 2][r]) t++;
                else if (grid[m / 2][l] == 1) cnt1 += 2;
                l++;
                r--;
            }
            res += t;
            if (cnt1 % 4 != 0 && t == 0) res += 2;
        } else if (n % 2 == 1) {
            int cnt1 = 0, t = 0;
            int l = 0, r = m - 1;
            while (l < r) {
                if (grid[l][n / 2] != grid[r][n / 2]) t++;
                else if (grid[l][n / 2] == 1) cnt1 += 2;
                l++;
                r--;
            }
            res += t;
            if (cnt1 % 4 != 0 && t == 0) res += 2;
        }
        return res;
    }
}
