class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        flips = 0
        one_count = 0
        wild = False
        x = len(grid[0])
        y = len(grid)

        for i in range(x//2):
            for j in range(y//2):
                cells = [grid[j][i], grid[j][-i-1], grid[-j-1][i], grid[-j-1][-i-1]]
                c = cells.count(0)
                if c == 1 or c == 3:
                    flips += 1
                elif c == 2:
                    flips += 2

        if x%2==1:
            p = x//2
            for j in range(y//2):
                if grid[j][p] != grid[-j-1][p]: 
                    flips += 1
                    wild = True
                else:
                    if grid[j][p] == 1:
                        one_count += 1
        if y%2==1:
            p = y//2
            for j in range(x//2):
                if grid[p][j] != grid[p][-j-1]: 
                    flips += 1
                    wild = True
                else:
                    if grid[p][j] == 1:
                        one_count += 1

        if x%2==1 and y%2==1:
            if grid[y//2][x//2] == 1: flips += 1

        if one_count%2==1 and not wild:
            flips += 2

        return flips
