class Solution:
    def minFlips(self, a: List[List[int]]) -> int:
        m, n = len(a), len(a[0])
        ans = 0
        for i in range(m // 2):
            for j in range(n // 2):
                cnt = a[i][j] + a[i][n - j - 1] + a[m - i - 1][j] + a[m - i - 1][n - j - 1]
                ans += min(cnt, 4 - cnt)
        counter = [0] * 3
        if m % 2 == 1:
            i = m // 2
            for j in range(n // 2):
                ones = a[i][j] + a[i][n - j - 1]
                counter[ones] += 1
        if n % 2 == 1:
            j = n // 2
            for i in range(m // 2):
                ones = a[i][j] + a[m - i - 1][j]
                counter[ones] += 1
        if counter[2] * 2 % 4 == 2 and counter[1] == 0:
            ans += 2
        else:
            ans += counter[1]
        if m % 2 == 1 and n % 2 == 1:
            ans += a[m // 2][n // 2]
        return ans
