class Solution:
    def minFlips(self, grid):
        n = len(grid)
        m = len(grid[0])
        ans = 0
        cnt = 0
        for i in range(n // 2):
            for j in range(m // 2):
                a = [grid[i][j], grid[i][m - 1 - j], grid[n - 1 - i][j], grid[n - 1 - i][m - 1 - j]]
                ans += 4 - max(a.count(1), a.count(0))
        print(ans)
        fl = False
        cnt = 0
        if n % 2 == 1:
            for j in range(m // 2):
                if grid[n // 2][j] != grid[n // 2][m - 1 - j]: fl = True
                if grid[n // 2][j] == grid[n // 2][m - 1 - j] and grid[n // 2][m - 1 - j] == 1: cnt += 1
                ans += grid[n // 2][j] != grid[n // 2][m - 1 - j]
        print(ans)
        if m % 2 == 1:
            for i in range(n // 2):
                if grid[i][m // 2] != grid[n - 1 - i][m // 2]: fl = True
                if grid[i][m // 2] == grid[n - 1 - i][m // 2] and grid[n - 1 - i][m // 2] == 1: cnt += 1
                ans += grid[i][m // 2] != grid[n - 1 - i][m // 2]
        print(ans)
        if n % 2 == 1 and m % 2 == 1 and grid[n // 2][m // 2] == 1:
            ans += 1
        if not fl and cnt % 2 == 1:
            ans += 2
        return ans
