class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n =grid.size(),m=grid[0].size();
        int ans=0;
        for(int i=0;i<n/2;i++){
          for(int j=0;j<m/2;j++){
            int one =0;
            if(grid[i][j]==1)one++;
            if(grid[n-i-1][j]==1)one++;
            if(grid[i][m-j-1]==1)one++;
            if(grid[n-i-1][m-j-1]==1)one++;
            ans+=min(4-one,one);
          }
        }
        cout<<ans<<endl;
