        //   ans+=min(4-one,one);
        //   if(grid[n/2][m/2]==1)ans++;
        // }
          int one=0,zero=m/2;
        if(n%2==1){
          one=0;
          zero=0;
          for(int i=0;i<m/2;i++){
            if(grid[n/2][i]==grid[n/2][m-i-1]){
              if(grid[n/2][i])one++;
              else zero++;
            }
          }
        }
          int onem=0,zerom=n/2;
        if(m%2==1){
          onem=0;
