class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int ans = 0;

        int n = grid.size(), m = grid[0].size();
        int twoCount = 0, oneCount = 0, zeroOne = 0;
        for(int i = 0; i < n / 2 + (n % 2); i++){
            for(int j = 0; j < m / 2 + (m % 2); j++){
                int one = 0, zero = 0;

                if(grid[i][j] == 0)zero++;
                else one++;

                int g = m - 1 - j, h = n - 1 - i;

                if(g != j && grid[i][g] == 0)zero++;
                else if(g != j && grid[i][g] == 1) one++;

                if(h != i && grid[h][j] == 0)zero++;
                else if(h != i && grid[h][j] == 1)one++;

                if(g != j && h != i && grid[h][g] == 0)zero++;
                else if(g != j && h != i && grid[h][g] == 1)one++;

                if(zero + one == 4){
                    ans += 4 - max(one, zero);
                }
                if(zero + one == 2){
                    if(one == 2){
                        twoCount++;
                    }else if(one == 1){
                        zeroOne++;
                    }
                }
                if(zero + one == 1){
                    if(one)ans++;
                }
            }
        }

        int p = 2 * twoCount;
        p %= 4;

        if(p == 2 && zeroOne){
            ans++;
            p = 0;
            zeroOne--;
        }

        ans += p + zeroOne;
        return ans;

    }
};
