class Solution {
public:
    int minFlips(vector<vector<int>>& grid) 
    {
        int n=grid.size();
        int m=grid[0].size();
        int ans=0;
        for(int i=0; i<n/2; i++)
        {
            int j=0;
            while(j<m/2)
            {
                int zero=0;
                int one=0;
                if(grid[i][j]==1)one++;
                else zero++;
                if(grid[i][m-j-1]==1)one++;
                else zero++;
                if(grid[n-i-1][j]==1)one++;
                else zero++;
                if(grid[n-i-1][m-j-1]==1)one++;
                else zero++;

                ans+=min(zero,one);
                j++;
            }
        }
        int cntones=0;
        int npairs=0;
        if(n%2!=0) 
        {
            int start=0;
            int end=m-1;
            while(start<end)
            {
                if(grid[n/2][start]==grid[n/2][end])
                {
                    if(grid[n/2][start]==1)
                    {
                        cntones+=2;
                    }
                }
                else
                {
                    ans++;
                    npairs++;
                }
                start++;
                end--;
            }
        }
        if(m%2!=0)
        {
            int start=0;
            int end=n-1;
            while(start<end)
            {
                if(grid[start][m/2]==grid[end][m/2])
                {
                    if(grid[start][m/2]==1)
                    {
                        cntones+=2;
                    }
                }
                else
                {
                    ans++;
                    npairs++;
                }
                start++;
                end--;
            }
        }
        if(m%2!=0 && n%2!=0 && grid[n/2][m/2]==1)
        {
            ans++;
        }
        if(cntones%4!=0 && npairs==0)
        {
            ans+=2;
        }
        return ans;
    }
};
