class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size()-1;
        int n = grid[0].size()-1;
        int res = 0;
        int isum = 0,judge =0;
        if(m!=0 && n!=0){
            for(int i = 0;i<=(m-1)/2;++i){
            for(int j = 0;j<=(n-1)/2;++j){
                int cursum = grid[i][j] + grid[m-i][j] + grid[i][n-j] + grid[m-i][n-j]; 
                if(cursum == 2){
                    res += 2;
                }else if(cursum == 1 || cursum == 3){
                    res += 1;
                }
            }
        }
        }

        if(m%2==0 && n!=0){

                for(int j = 0;j<=(n-1)/2;++j){
                    if(grid[m/2][j] != grid[m/2][n-j]){
                        res+=1;
                        judge = 1;
                    }else{
                        isum+=grid[m/2][j]*2;
                    }
                }

        }
        if(n%2==0 && m!=0){

                for(int i =0;i<=(m-1)/2;++i){
                    if(grid[i][n/2] != grid[m-i][n/2]){
                        res+=1;
                        judge = 1;
                    }else{
                        isum+=grid[i][n/2]*2;
                    }
                }

        }
        if(n%2==0 && m%2==0){
            res += grid[m/2][n/2];
        }
        if(isum%4 == 2 && (judge == 0)){
            res += 2;
        }
        return res;
    }
};
