class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(),m=grid[0].size();
        int a1=0;
        for(int i=0; i<n/2; i++){
            for(int j=0; j<m/2; j++){
                int o=0;
                if(grid[i][j]) o++;
                if(grid[i][m-1-j]) o++;
                if(grid[n-1-i][j]) o++;
                if(grid[n-1-i][m-1-j]) o++;
                a1+=(min(o,4-o));
            }
        }
        //cout<<a1<<endl;
        int ty1=0,ty2=0,ty3=0;
        if(n&1){
            for(int j=0; j<m/2; j++){
                if(grid[n/2][j]==0&&grid[n/2][m-1-j]==0) ty1++;
                else if(grid[n/2][j]&&grid[n/2][m-1-j]) ty2++;
                else ty3++;
                //cout<<ty1<<ty2<<ty3<<endl;
            }
        }
        if(m&1){
            for(int j=0; j<n/2; j++){
                if(grid[j][m/2]==0&&grid[n-1-j][m/2]==0) ty1++;
                else if(grid[j][m/2]&&grid[n-1-j][m/2]) ty2++;
                else ty3++;
                //cout<<ty1<<ty2<<ty3<<endl;
            }
        }
        //cout<<ty1<<ty2<<ty3<<endl;
        if((n&1)&&(m&1)&&grid[n/2][m/2]) a1++;
        if(ty3) return a1+ty3;
        if(ty2&1) return a1+2;
        return a1;
    }
};
