class Solution {
    public int minFlips(int[][] grid) {
        int count = 0;
        // 1. 除了中间十字架意外，每四个配对
        for (int i = 0; i < grid.length / 2; i++) {
            for (int j = 0; j < grid[0].length / 2; j++) {
                int[] nums = new int[4];
                nums[0] = grid[i][j];
                nums[1] = grid[i][grid[0].length-1-j];
                nums[2] = grid[grid.length-i-1][j];
                nums[3] = grid[grid.length-i-1][grid[0].length-1-j];
                int zero = 0;
                int one = 0;
                for (int k: nums) {
                    if (k == 0) {
                        zero++;
                    } else if (k == 1) {
                        one++;
                    }
                }
                count += Math.min(zero, one);
            }
        }


        // 中间一点
        if (grid.length % 2 == 1 && grid[0].length % 2 == 1) {
            if (grid[grid.length/2][grid[0].length/2] == 1) {
                count++;
                grid[grid.length/2][grid[0].length/2] = 0;
            }
        }

        //System.out.println(count);


        // 2. 考虑中间十字架
        int crossCount = 0;
        if (grid.length % 2 == 1) {
            for (int i = 0; i < grid[0].length / 2; i++) {
                if (grid[grid.length / 2][i] != grid[grid.length / 2][grid[0].length-i-1]) {
                    crossCount++;
                }
            }
        }

        if (grid[0].length % 2 == 1) {
            for (int i = 0; i < grid.length / 2; i++) {
                if (grid[i][grid[0].length/2] != grid[grid.length-i-1][grid[0].length/2]) {
                    crossCount++;
                }
            }
        }

        // 十字架求和
        int crossSum = 0;
        if (grid.length % 2 == 1) {
            for (int i = 0; i < grid[0].length; i++) {
                crossSum += grid[grid.length / 2][i];
            }
        }

        if (grid[0].length % 2 == 1) {
            for (int i = 0; i < grid.length; i++) {
                crossSum += grid[i][grid[0].length/2];
            }
        }

        count += crossCount;

        if (crossCount==0 && crossSum % 4 != 0) {
            count += 2;
        }

       return count;
    }
}
