class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        // vector<vector<int>> g;
        vector<int> g;
        vector<int> f;
        for (int l = 0, r = n - 1; l < r; ++l, --r) {
            for (int u = 0, d = m - 1; u < d; ++u, --d) {
                g.push_back(grid[l][u] + grid[r][u] + grid[l][d] + grid[r][d]);
            }
        }
        if (n % 2 == 1) {
            for (int i = 0, j = m - 1; i < j; ++i, --j) {
                f.push_back(grid[n / 2][i] + grid[n / 2][j]);
            }
        }
        if (m % 2 == 1) {
            for (int i = 0, j = n - 1; i < j; ++i, --j) {
                f.push_back(grid[i][m / 2] + grid[j][m / 2]);
            }
        }
        int ct = 0, c4 = 0;
        if (n % 2 == 1 && m % 2 == 1) {
            ct = grid[n / 2][m / 2];
        }
        array<int, 3> c1{0, n * m * 2, n * m * 2}, t;
        for (int i : g) {
            // t[0] = min(c1[0] + (i < 3 ? i : 4 - i), c1[2] + abs(2 - i));
            // t[2] = min(c1[2] + (i < 3 ? i : 4 - i), c1[0] + abs(2 - i));
            // c1[0] = t[0];
            // c1[2] = t[2];
            c4 += (i < 3 ? i : 4 - i);
        }
        for (int i : f) {
            // cout << i << ' ';
            t[0] = min(c1[0] + i, c1[2] + abs(2 - i));
            t[2] = min(c1[2] + i, c1[0] + abs(2 - i));
            c1[0] = t[0];
            c1[2] = t[2];
        }
        // cout << c1[0] << endl;
        return c1[0] + ct + c4;
    }
};
