class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int m = grid.size();
        int n = grid[0].size();
        int ans = 0;
        for(int i=0;i<m/2;i++) {
            for(int j=0;j<n/2;j++) {
                int one=0,zero=0;
                if(grid[i][j] == 0) {
                    zero++;
                } else {
                    one++;
                }
                if(grid[m-1-i][j] == 0) {
                    zero++;
                } else {
                    one++;
                }
                if(grid[i][n-1-j] == 0) {
                    zero++;
                } else {
                    one++;
                }
                if(grid[m-1-i][n-1-j] == 0) {
                    zero++;
                } else {
                    one++;
                }
                ans += min(one,zero);
            }
        }
        if(m%2!=0 && n%2!=0) {
            int oneh = 0;
            int xh = 0;
            for(int j=0;j<n/2;j++) {
                if(grid[m/2][j] != grid[m/2][n-1-j]) {
                    xh++;
                } else if(grid[m/2][j] == 1) {
                    oneh+=1;
                }
            }
            int oner = 0;
            int xr = 0;
            for(int i=0;i<m/2;i++) {
                if(grid[i][n/2] != grid[m-1-i][n/2]) {
                    xr++;
                }else if(grid[i][n/2] == 1) {
                    oner++;
                }
            }
            if((oneh+oner)%2==0) {
                ans+=(xh+xr);
            } else {
                if(xh+xr>0) {
                    ans+=(xh+xr);
                } else {
                    ans+=2;
                }
            }
            if(grid[m/2][n/2]==1) {
                ans+=1;
            }
        } else if (m%2!=0) {
            int one = 0;
            int x = 0;
            for(int j=0;j<n/2;j++) {
                if(grid[m/2][j] != grid[m/2][n-1-j]) {
                    x++;
                    one++;
                } else if(grid[m/2][j] == 1) {
                    one+=2;
                }
            }
            int rem = one-x;
            if(rem%4==0) {
                ans+=x;
            } else if(rem%4==2) {
                if(x>0) {
                    ans+=x;
                } else {
                    ans+=2;
                }
            }
        } else if (n%2!=0) {
            int one = 0;
            int x = 0;
            for(int i=0;i<m/2;i++) {
                if(grid[i][n/2] != grid[m-1-i][n/2]) {
                    x++;
                    one++;
                }else if(grid[i][n/2] == 1) {
                    one+=2;
                }
            }
            int rem = one-x;
            if(rem%4==0) {
                ans+=x;
            } else if(rem%4==2) {
                if(x>0) {
                    ans+=x;
                } else {
                    ans+=2;
                }
            }
        }
        return ans;
    }
};
