class Solution {
public:

    int minFlips(vector<vector<int>>& grid) {

        int row=grid.size();
        int col=grid[0].size();

        int x=0;
        set<pair<int,int>> flip;
        int one=0;
        int zer=0;

        int ans=0;
        for(int i=0; i<row/2; i++){
            for(int j=0; j<col/2; j++){
                map<int,int> c;
                c[grid[i][j]]++;
                c[grid[i][col-j-1]]++;
                c[grid[row-i-1][j]]++;
                c[grid[row-i-1][col-j-1]]++;
                ans+=min(4-c[0],4-c[1]);
            }
        }
        int extra=0;
        if(row%2==1){
            for(int i=0; i<col/2; i++){
                if(grid[row/2][i]!=grid[row/2][col-i-1]) extra++;

            }
            for(int i=0; i<col; i++){
                if(grid[row/2][i]==1) one++;

            }
        }
        if(col%2==1){
            for(int i=0; i<row/2; i++){
                if(grid[i][col/2]!=grid[row-i-1][col/2]) extra++;
            }
            for(int i=0; i<row; i++){
                if(grid[i][col/2]==1) one++;

            }
        }
        if(row%2==1 && col%2==1 && grid[row/2][col/2]==1) one--;
        ans+=extra;
        // cout<<one<<" "<<extra<<endl;
        int temp=ans+(one-extra)%4;
        for(int i=1; i<=extra; i++){
            int gain=i-(extra-i);
            temp=min(temp,ans+(one+gain)%4);
        }
        return temp;

    }
};
