class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();

        int count = 0;
        int ss = 0;
        int ssss = 0;
        int sss = 0;
        int st = 0;
        int s2 = 0;

        for(int i = 0; i < (rows + 1) / 2; i++) {
            for(int j = 0; j < (cols + 1) / 2; j++) {
                if(i == rows - 1 - i && j == cols - 1 - j) {
                    if(grid[i][j] == 1) count++;
                }
                else if(i == rows - 1 - i) {
                    int temp = grid[i][j] + grid[i][cols - 1 - j];
                    if(temp == 1) st++;
                    else if(temp == 2) s2++;
                }
                else if(j == cols - 1 - j) {
                    int temp = grid[i][j] + grid[rows - 1 - i][j];
                    if(temp == 1) st++;
                    else if(temp == 2) s2++;
                }
                else {
                    int temp = grid[i][j] + grid[rows - 1 - i][j] + grid[i][cols - 1 - j] + grid[rows - 1 - i][cols - 1 - j];
                    if(temp == 1) ss++;
                    else if(temp == 2) ssss++;
                    else if(temp == 3) sss++;
                }                                    
            }
        }
        count += (ss + sss + 2 * ssss + st);

        if(s2 % 2 != 0) {
            if(st <= 0) count += 2;
        }

        return count;
    }
};
