class Solution {
public:
    int minFlips(vector<vector<int>>& v) {
        int n=v.size();
        int m=v[0].size();
        int ans=0;
        vector<int>zz(5);
        for(int i=0;i<n/2;i++){
            for(int j=0;j<m/2;j++){
                int z=0;
                z+=((v[i][j]==1)+(v[i][m-1-j]==1)+(v[n-1-i][j]==1)+(v[n-1-i][m-1-j]==1));
                if(z==1) ans++;
                if(z==2) ans+=2;
                if(z==3) ans++;
                zz[z]++;
            }
        }
        if((n%2 == 0) && (m%2 == 0)) return ans;
        if((n%2 == 0) || (m%2 == 0)){
                int sum=0;
            if(n%2){
                for(int j=0;j<m/2;j++){
                    sum+=((v[n/2][j]!=v[n/2][m-1-j]));
                }
                if(sum==0){
                    for(int j=0;j<m;j++) sum+=(v[n/2][j]==1);
                    sum%=4;
                }
            }
            else{
                for(int i=0;i<n/2;i++){
                    sum+=((v[i][m/2]!=v[n-1-i][m/2]));
                }
                if(sum==0){
                    for(int i=0;i<n;i++){
                        sum+=((v[i][m/2]==1));
                    }   
                sum%=4;
                }
            }
            return sum+ans;
        }
        int sum=0;

        for(int j=0;j<m/2;j++){
            sum+=((v[n/2][j]!=v[n/2][m-1-j]));
        }
        for(int i=0;i<n/2;i++){
            sum+=((v[i][m/2]!=v[n-1-i][m/2]));
        }
        if(sum==0){
            for(int j=0;j<m;j++){
                sum+=((v[n/2][j]==1));
            }
            for(int i=0;i<n;i++){
                sum+=((v[i][m/2]==1));
            } 
            sum-=2*(v[n/2][m/2]==1);
            sum%=4;
        }
        return sum+ans+(v[n/2][m/2]==1);
    }
};
