class Solution:
    def minFlips(self, grid: List[List[int]]) -> int:
        rows=len(grid)
        cols=len(grid[0])
        v=[[0 for j in range(cols)] for i in range(rows)]
        ans=0
        x=0
        y=0
        for i in range(rows):
            for j in range(cols):
                if v[i][j]==0:
                    s=[[i,j],[i,cols-j-1],[rows-i-1,j],[rows-i-1,cols-j-1]]
                    zero=0
                    one=0
                    for [a,b] in s:
                        if a>=0 and a<rows and b>=0 and b<cols and v[a][b]==0:
                            v[a][b]=1
                            if grid[a][b]==0:
                                zero+=1
                            else:
                                one+=1

                    if zero+one==1:
                        ans+=one
                    elif zero+one==4:
                        ans+=min(zero,one)
                    else:
                        if one==1:
                            x+=1
                        elif one==2:
                            y+=1

        y=y%2
        if y==0:
            ans+=x
        else:
            if x==0:
                ans+=2
            elif x==1:
                ans+=1
            elif x>=2:
                ans+=x
        return ans
