class Solution {
public:
    int minFlips(vector<vector<int>>& grid) {
        int n=grid.size(), m = grid[0].size();
        int ans=0;
        for (int i=0;i<n/2;i++)
        {
            for (int j=0;j<m/2;j++)
            {
                int i1 = n-1-i, j1 = m-1-j;
                int o=0;
                if (grid[i][j])o++;
                if (grid[i1][j])o++;
                if (grid[i][j1])o++;
                if (grid[i1][j1])o++;
                if (o<=2)ans+=o;
                else{
                    ans += 4-o;
                }
            }
        }
        // cout<<ans<<endl;
        int temp=0, cnt=0;
        if (m%2)
        {
            int l=0, r=n-1;
            while (l<r)
            {
                if (grid[l][(m/2)]!=grid[r][m/2])
                {
                    ans++;
                    cnt++;
                }
                else if (grid[l][m/2]==1)
                {
                    temp++;
                }
                l++;r--;
            }
        }
        // cout<<ans<<endl;
        if (n%2)
        {
            int l=0, r=m-1;
            while (l<r)
            {
                if (grid[n/2][l]!=grid[n/2][r])
                {
                    ans++;
                    cnt++;
                }
                else if (grid[n/2][l]==1)temp++;
                l++;r--;
            }
        }
        // cout<<ans<<endl;
        if (temp%2 && cnt==0)ans+=2;
        // cout<<ans<<endl;
        if (n%2 && m%2 && grid[n/2][m/2])ans++;
        return ans;
    }
};
